"-- Gravity.lua\r\n\r\nreturn function(MainTab, MainSection)\r\n    local Slider = MainTab:CreateSlider({\r\n        Name = \"Gravity (No Fall Damage)\",\r\n        Range = {1, 500},\r\n        Increment = 1,\r\n        Suffix = \"Gravity\",\r\n        CurrentValue = game.Workspace.Gravity,\r\n        Flag = \"slidergravity\",\r\n        Callback = function(Value)\r\n            game.Workspace.Gravity = Value\r\n        end,\r\n    })\r\nend\r\n"